HttpClientHandler clientHandler = new HttpClientHandler();
HttpClient client = new HttpClient(clientHandler);
client.Timeout = Timeout.InfiniteTimeSpan;
HttpRequestMessage request = new HttpRequestMessage(new HttpMethod("POST"), "https://postman-echo.com/post/?hardik=\"me\"");
IList<KeyValuePair<string, string>> formData = new List<KeyValuePair<string, string>>();
formData.Add(new KeyValuePair<string, string>("1", "a"));
formData.Add(new KeyValuePair<string, string>("2", "b"));
formData.Add(new KeyValuePair<string, string>("\"\"12\"\"", "\"23\""));
formData.Add(new KeyValuePair<string, string>("'1\"2\\\"\"3'", "'1\"23\"4'"));
request.Content = new FormUrlEncodedContent(formData);
HttpResponseMessage response = await client.SendAsync(request);
string responseBody = await response.Content.ReadAsStringAsync();
Console.WriteLine(responseBody);
