var client = new HttpClient();
var request = new HttpRequestMessage(HttpMethod.Post, "https://postman-echo.com/post");
var collection = new List<KeyValuePair<string, string>>();
collection.Add(new KeyValuePair<string, string>("1", "'a'"));
collection.Add(new KeyValuePair<string, string>("2", "\"b\""));
collection.Add(new KeyValuePair<string, string>("'3'", "c"));
collection.Add(new KeyValuePair<string, string>("\"4\"", "d"));
collection.Add(new KeyValuePair<string, string>("Special", "!@#$%*()^_=`&~              "));
collection.Add(new KeyValuePair<string, string>("more", ",./';[]}{\":?><|\\\\    "));
collection.Add(new KeyValuePair<string, string>("non-ascii", "테스트"));
var content = new FormUrlEncodedContent(collection);
content.Headers.ContentType = new MediaTypeHeaderValue("application/x-www-form-urlencoded");
request.Content = content;
var response = await client.SendAsync(request);
response.EnsureSuccessStatusCode();
Console.WriteLine(await response.Content.ReadAsStringAsync());
